mapper.select:
SELECT no,id,subject,TO_CHAR(regdate, 'YYYY-MM-DD') as dbday,hit,group_step,group_id, num
FROM (SELECT no,id,subject,regdate,hit,group_step,group_id,rownum as num
    FROM (SELECT no,id,subject,regdate,hit,group_step,group_id
          FROM replyboard 
          ORDER BY group_id DESC, group_step ASC)
    WHERE rownum <= #{end}
)
WHERE num >= #{start}

SELECT no,subject,content,id,isreply,TO_CHAR(regdate,'YYYY-MM-DD') as dbday
FROM replyboard
WHERE no=#{no}

mapper.update:
UPDATE replyboard SET 
isreply=1
WHERE no=#{no}

UPDATE replyboard SET 
isreply=0
WHERE no=#{no}
