mapper.select:
SELECT no,id,subject,TO_CHAR(regdate, 'YYYY-MM-DD') as dbday,hit,group_step,group_id, name, isreply, num
FROM (SELECT no,id,subject,regdate,hit,group_step,group_id, name,isreply, rownum as num
    FROM (SELECT no,id,subject,regdate,hit,group_step,group_id, name,isreply
          FROM replyboard 
          ORDER BY group_id DESC, group_step ASC)
    WHERE rownum <= #{end}
)
WHERE num >= #{start}

mapper.insert:
INSERT INTO replyboard(no,id,subject,content,group_id, name) 
VALUES(pr_no_seq.nextval,#{id},#{subject},#{content},
 (SELECT NVL(MAX(group_id)+1,1) FROM replyboard), #{name}
)

mapper.select:
SELECT no,subject,content,id,isreply,TO_CHAR(regdate,'YYYY-MM-DD') as dbday, name, group_id, group_step
FROM replyboard
WHERE no=#{no}

mapper.update:
UPDATE replyboard SET 
isreply=1
WHERE no=#{no}

UPDATE replyboard SET 
isreply=0
WHERE no=#{no}
